import React from 'react';
import PropTypes from 'prop-types';

const TextInput = ({ name, label, type, placeholder, onChange, onBlur, errors }) => {
    const error = (errors && <div className="color-red">{errors}</div>);

    return (
        <div className="form-group">
            <label htmlFor={name}>{label}</label>
            <input type={type} className="form-control" id={name} name={name} placeholder={placeholder} onChange={onChange} onBlur={onBlur} />
            {error}
        </div>
    );
}

TextInput.propTypes = {
    name: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    type: PropTypes.string.isRequired,
    placeholder: PropTypes.string.isRequired,
    onChange: PropTypes.func.isRequired,
    onBlur: PropTypes.func.isRequired
};

export default TextInput;

