import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router';
import { connect } from 'react-redux';
import {
    getTotalBasketCount,
    getTotalBasketPrices
} from 'selectors';

class BasketCart extends Component {
    static propTypes = {
        totalCount: PropTypes.number.isRequired,
        totalPrices: PropTypes.number.isRequired
    }

    render() {
        const { totalCount, totalPrices } = this.props;
        return (
            <div className='cart'>
                <div className='dropdown'>
                    <Link
                        to='/basket'
                        id='dLabel'
                        className='btn btn-inverse btn-block btn-lg'
                    >
                        <i className='fa fa-fa-shopping-cart' />
                        <span>{totalCount} item(s) - $ {totalPrices}</span>
                    </Link>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    totalCount: getTotalBasketCount(state),
    totalPrices: getTotalBasketPrices(state)
});

export default connect(mapStateToProps)(BasketCart);
