import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { browserHistory } from 'react-router';

import { loginAction } from 'actions';

class Login extends Component {
    constructor(props){
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            email: '',
            password: ''
        };
    }

    static propTypes = {
        loginAction: PropTypes.func
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    async handleSubmit(e) {
        e.preventDefault();
        await this.props.loginAction(this.state);
        browserHistory.push(this.props.location.state.lastRoute);
    }

    render() {
        return (
            <div className="col-md-4 col-md-offset-4 loginForm">
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input type="email" className="form-control" id="email" name="email" placeholder="Enter email" onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Пароль</label>
                        <input type="password" className="form-control" id="password" name="password" placeholder="Password" onChange={this.handleChange} />
                    </div>
                    <button type="submit" className="btn btn-success">Login</button>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = {
    loginAction
};

export default connect(null, mapDispatchToProps)(Login);
