import { 
    ADD_PHONE_TO_BASKET, 
    REMOVE_PHONE_FROM_BASKET,
    CLEAN_BASKET,
    BASKET_CHECKOUT
} from "actionTypes";

const initialState = [];

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case ADD_PHONE_TO_BASKET:
            return [].concat(state, payload);

        case REMOVE_PHONE_FROM_BASKET:
            return state.filter(id => id !== payload);

        case CLEAN_BASKET:
            return [];

        case BASKET_CHECKOUT:
            console.log(payload);
            return state; 
            
        default: 
            return state;
    }
};