import { FETCH_PHONES_SUCCESS, LOAD_MORE_PHONES_SUCCESS } from "actionTypes";

const initialState = [];

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            const newIds = payload.map(phone => phone.id);
            return [].concat(state, newIds);

        case LOAD_MORE_PHONES_SUCCESS:
            const moreIds = payload.map(phone => phone.id);
            return [].concat(state, moreIds);

        default:
            return state;
    }
}