import * as EmailValidator from 'email-validator';

export default (name, value) => {
    switch (name) {
        case 'email':
            if (!EmailValidator.validate(value)) {
                return 'Введен некорректный email. Попробуйте снова.';
            }
            break;
        case 'password':
            if (value.length < 8) {
                return 'Пароль не должен быть короче 8 символов.';
            }
            break;
        default:
            return '';
    }
}
