export default (name, value) => {
    switch (name) {
        case 'email':
            if (value.length < 8) {
                return 'Email short';
            }
            break;
        case 'password':
            if (value.length < 8) {
                return 'Password short';
            }
            break;
        default: 
            return '';
    }
}

